INSERT INTO [dbo].[mdiy_tag_sql] ([id], [tag_id], [tag_sql], [sort]) VALUES (N'5', N'3', N'<#assign _typeid=""/>
<#if column?? && column.categoryId??>
	<#assign  _typeid="${column.categoryId}">
</#if>
<#if typeid??>
	<#assign  _typeid="${typeid}">
</#if>
SELECT
	basic_id AS id,
	@rownum := @rownum + 1 AS `index`,
	LEFT (basic_title, ${titlelen ?default(40)}) AS title,
	basic_title AS fulltitle,
	article_author AS author,
	article_source AS source,
	article_content AS content,
	category.category_title AS typename,
	category.category_id AS typeid,
	category.category_smallimg AS typelitpic,
	<#--列表页动态链接-->
	<#if isDo?? && isDo>
	CONCAT("/${modelName}/list.do?typeid=", category.category_id) as typelink,
	<#else>
	(SELECT "index.html") AS typelink,
	</#if>
	basic.basic_thumbnails AS litpic,
	<#--内容页动态链接-->
	<#if isDo?? && isDo>
	CONCAT("/${modelName}/view.do?id=", basic_id) as link,
	<#else>
	cms_article.article_url AS link,
	</#if>
	basic_datetime AS date,<#if tableName??>${tableName}.*,</#if>
	basic_description AS descrip,
	basic_hit AS hit,
	article_type AS flag,
	category_title AS typetitle,
	cms_article.article_keyword AS keyword 
FROM
	(SELECT @rownum := 0) r,
	basic
	LEFT JOIN cms_article ON cms_article.article_basicid = basic.basic_id
	LEFT JOIN category ON basic_categoryid = category.category_id
	LEFT JOIN basic_column ON basic_column.column_category_id = basic.basic_categoryid 
	<#--判断是否有自定义模型表-->
	<#if tableName??>LEFT JOIN ${tableName} ON ${tableName}.basicId=cms_article.article_basicid </#if>
WHERE  <#--查询栏目-->
	basic.basic_display=0 
	<#--根据模块编号查询分类-->
	<#if column?? && column.categoryModelId?has_content>
		and category_modelid=${column.categoryModelId}
	</#if>
	<#--判断是否有搜索分类集合-->
	<#if  _typeid?has_content>
		and (basic_categoryid=${_typeid} or basic_categoryid in 
		(select category_id FROM category where category.del=0 and find_in_set(${_typeid},CATEGORY_PARENT_ID)))
	<#else>
		<#if search??> 	
			<#if search.categoryIds??>and FIND_IN_SET(category_categoryid,''${search.categoryIds}'')</#if>
			<#--标题-->
			<#if search.basic_title??> and basic_title like CONCAT("%",''${search.basic_title}'',"%")</#if>
			<#--作者-->
			<#if search.article_author??> and article_author like CONCAT("%",''${search.article_author}'',"%")</#if>
			<#--来源-->
			<#if search.article_source??> and article_source like CONCAT("%",''${search.article_source}'',"%")</#if>
			<#--属性-->
			<#if search.article_type??> and article_type like CONCAT("%",''${search.article_type}'',"%")</#if>
			<#--图片-->
			<#if search.basic_thumbnails??> and basic_thumbnails like CONCAT("%",''${search.basic_thumbnails}'',"%")</#if>
			<#--描述-->
			<#if search.basic_description??> and basic_description like CONCAT("%",''${search.basic_description}'',"%")</#if>
			<#--关键字-->
			<#if search.article_keyword??> and article_keyword like CONCAT("%",''${search.article_keyword}'',"%")</#if>
			<#--内容-->
			<#if search.article_content??> and article_content like CONCAT("%",''${search.article_content}'',"%")</#if>
			<#--自定义顺序-->
			<#if search.article_freeorder??> and article_freeorder=${search.article_freeorder}</#if>
		</#if>
 </#if>
	<#--标题-->
	<#if basic_title??> and basic_title like CONCAT("%",''${basic_title}'',"%")</#if>
	<#--作者-->
	<#if article_author??> and article_author like CONCAT("%",''${article_author}'',"%")</#if>
	<#--来源-->
	<#if article_source??> and article_source like CONCAT("%",''${article_source}'',"%")</#if>
	<#--属性-->
	<#if article_type??> and article_type like CONCAT("%",''${article_type}'',"%")</#if>
	<#--图片-->
	<#if basic_thumbnails??> and basic_thumbnails like CONCAT("%",''${basic_thumbnails}'',"%")</#if>
	<#--描述-->
	<#if basic_description??> and basic_description like CONCAT("%",''${basic_description}'',"%")</#if>
	<#--关键字-->
	<#if article_keyword??> and article_keyword like CONCAT("%",''${article_keyword}'',"%")</#if>
	<#--内容-->
	<#if article_content??> and article_content like CONCAT("%",''${article_content}'',"%")</#if>
	<#--自定义顺序-->
	<#if article_freeorder??> and article_freeorder=${article_freeorder}</#if>
	<#--自定义模型-->
	<#if diyModel??> 
		<#list diyModel as dm>
			and ${tableName}.${dm.key} = "${dm.value}" 
		</#list>
	</#if>
	<#--文章属性-->
	<#if flag?? >
			and cms_article.article_type like CONCAT(''%'',''${flag}'',''%'') 
	</#if>
	<#if noflag?? >
			and cms_article.article_type not like CONCAT(''%'',''${noflag}'',''%'') 
	</#if>
	<#--字段排序-->
	<#if orderby?? >
		ORDER BY 
			<#if orderby=="date"> basic.basic_datetime</#if> 
			<#if orderby=="updatedate"> basic.basic_updatetime</#if> 
			<#if orderby=="hit"> basic.basic_hit</#if>
			<#if orderby=="sort"> basic.basic_sort</#if>
			<#if orderby=="id"> basic.basic_id</#if>
	</#if>
	<#if order?? >
			<#if order=="desc"> desc</#if>
			<#if order=="asc"> asc</#if>
	</#if>
	 LIMIT 
	  <#--判断是否分页-->
	<#if ispaging?? && (pageTag.pageNo)??>${(pageTag.pageNo-1)*size?eval},${size?default(20)}
	<#else>${size?default(20)}</#if>
', N'1')
GO

INSERT INTO [dbo].[mdiy_tag_sql] ([id], [tag_id], [tag_sql], [sort]) VALUES (N'6', N'4', N'<#--freemarker格式化数字-->
<#setting number_format="#">
<#assign _typeid=""/>
<#if column?? && column.categoryId??>
	<#assign  _typeid="${column.categoryId}">
</#if>
<#if typeid??>
	<#assign  _typeid="${typeid}">
</#if>
select 
	@rownum := @rownum + 1 AS typeindex,
	category_id as id,
	category_id as typeid,
	category_title as typetitle,
	<#--返回父id集合-->
	category_parent_id as pids,
	<#--栏目选中的样式-->
	IF(<#if _typeid?has_content>${_typeid}<#else>0</#if> = category_id ,"${class!''''}","") as class,
	<#--动态链接-->
	<#if isDo?? && isDo>
	CONCAT("/${modelName}/list.do?typeid=", category_id) as typelink,
	<#else>
	CONCAT(column_path,"/index.html") as typelink,
	</#if>
	column_keyword as typekeyword,
	column_descrip as typedescrip,
	category_smallimg as typelitpic  
	from (SELECT @rownum := 0) r,basic_column bc  
	LEFT JOIN category on bc.column_category_id=category.category_id where 
	category.del=0 
	<#--根据模块编号查询分类-->
	<#if column?? && column.categoryModelId?has_content>
		and category_modelid=${column.categoryModelId}
	</#if>
<#if type?has_content>
	<#--顶级栏目（单个）-->
	<#if type=="top">
		and category_id=(select left(category_parent_id,LOCATE(",",category_parent_id)-1) from category where category_id = ${_typeid})
	<#elseif type=="nav">
		and(category_categoryid=0 or category_categoryid is null)
	<#--同级栏目（多个）-->
	<#elseif type=="level">
		and
		<#if _typeid?has_content>
		 category_categoryid=(select category_categoryid from category where category_id=${_typeid})
		<#else>
		 1=1
		</#if>
  	<#--当前栏目（单个）-->
	<#elseif type=="self">
		 and 
		 <#if _typeid?has_content>
		  category_id=${_typeid}
		 <#else>
		 1=1
		 </#if>
	<#--当前栏目的所属栏目（多个）-->
	<#elseif type=="path">
			and 
		 <#if _typeid?has_content>
		   category_id in (<#if column?? && column.categoryParentId??>${column.categoryParentId},</#if>${_typeid})
		 <#else>
		  1=1
		 </#if>
	<#--子栏目（多个）-->
	<#elseif type=="son">
			and 
		 <#if _typeid?has_content>
		  category_categoryid=${_typeid}
		 <#else>
		  1=1
		 </#if>
		 <#--上一级栏目没有则取当前栏目（单个）-->
	<#elseif type=="parent">
			and 
		 <#if _typeid?has_content>
		   <#if column?? && column.categoryCategoryId?? && column.categoryCategoryId!=0>
				category_id=${column.categoryCategoryId}
			 <#else>
			  category_id=${_typeid}
			 </#if>
		 <#else>
		  1=1
	</#if>
	<#--子栏目或同级栏目（多个）-->
	<#elseif type=="sonOrLevel">
		 and 
		 <#if _typeid?has_content>
	 	 category_categoryid= if((SELECT count(*) FROM category
		 LEFT JOIN basic_column bc ON bc.column_category_id = category.category_id 
		 WHERE category_categoryid=${_typeid})>0,${_typeid},(select category_categoryid from category where category_id=${_typeid}))
		 <#else>
		 1=1
		 </#if>
	</#if>
<#else> <#--默认son-->
	 and
	<#if _typeid?has_content>
	 category_categoryid=${_typeid}
	<#else>
	 (category_categoryid=0 or category_categoryid is null)
	</#if>
</#if>', N'1')
GO

